import React from 'react';
import './Navbar.css';
// import logo from "../../assets/logos/kl-high-resolution-logo-white-transparent.png";
import { Link } from 'react-scroll';
import sun from "../../assets/symbols/sun.svg";
import moon from "../../assets/symbols/moon.svg";

var icon = document.getElementById("toggle_icon");


document.addEventListener('DOMContentLoaded', function() {

  toggleTheme();
});

function toggleTheme(){
  document.body.classList.toggle("light_theme");
  if(document.body.classList.contains("light_theme")){
    icon.src = sun;
  }else{
    icon.src=moon;
  }
}

const Navbar = () => {
  return (
    <nav className='Navbar'>

        <div className="desktopMenu">
          <Link className="desktopMenuListItem " >About</Link>
            <Link className="desktopMenuListItem" >Experience</Link>
            <Link className="desktopMenuListItem" >Projects</Link>
            <Link className="desktopMenuListItem" >Contact</Link>

        </div>

        <button onClick={toggleTheme} id="apperance_b" >
            <img id="toggle_icon" class="apperance_icon" src="" alt="apperance"></img>

        </button>



    </nav>
  )
}




export default Navbar;
