import React, { useState, useEffect } from 'react';
import './Navbar.css';
import { Link } from 'react-scroll';
import sun from "../../assets/symbols/sun.svg";
import moon from "../../assets/symbols/moon.svg";

const Navbar = () => {
  const [theme, setTheme] = useState('dark'); 

  useEffect(() => {
    const icon = document.getElementById("toggle_icon"); 
    icon.src = theme === 'light' ? sun : moon;
    document.body.classList.toggle("light_theme", theme === 'light');
  }, [theme]);

  const toggleTheme = () => {
    setTheme(prevTheme => prevTheme === 'light' ? 'dark' : 'light');
  };

  return (
    <nav className='Navbar'>
      <div className="desktopMenu">
        <Link className="desktopMenuListItem">About</Link>
        <Link className="desktopMenuListItem">Experience</Link>
        <Link className="desktopMenuListItem">Projects</Link>
        <Link className="desktopMenuListItem">Contact</Link>
      </div>
      <></>
      <button onClick={toggleTheme} id="apperance_b">
        <img id="toggle_icon" className="apperance_icon" src="" alt="apperance" />
      </button>
    </nav>
  );
};

export default Navbar;
