import React, { useEffect, useRef } from 'react';
import VanillaTilt from "vanilla-tilt";
import './ImageCollage.css';

import guitar from "../../assets/CollagePhotos/guitar2.jpg"; 
import kcphoto from "../../assets/CollagePhotos/kcphoto3.jpg"; 
import laptop from "../../assets/CollagePhotos/laptop.jpg"; 
import BJJ from "../../assets/CollagePhotos/bjj_win.JPG"; 
import team from "../../assets/CollagePhotos/team_photo.JPG"; 

const ImageCollage = () => {
  const tiltRefs = useRef([]);

  useEffect(() => {
    tiltRefs.current.forEach(tiltRef => VanillaTilt.init(tiltRef, {
      max: 25,
      speed: 400,
      glare: true,
      "max-glare": 0.5,
    }));

    return () => {
      tiltRefs.current.forEach(tiltRef => tiltRef.vanillaTilt.destroy());
    };
  }, []);

  const addTiltRef = el => {
    if (el && !tiltRefs.current.includes(el)) {
      tiltRefs.current.push(el);
    }
  };

  return (
    <section className='ImageCollage'>
      <div className="Collage_Container">
        <img className="imageFormat" src={guitar} alt="" ref={addTiltRef} />
        <img className="imageFormat" src={kcphoto} alt="" ref={addTiltRef} />
        <img className="imageFormat" src={laptop} alt="" ref={addTiltRef} />
        <img className="imageFormat" src={BJJ} alt="" ref={addTiltRef} />
        <img className="imageFormat" src={team} alt="" ref={addTiltRef} />
      </div>
    </section>
  );
};

export default ImageCollage;
