import React, { useState } from 'react';
import Navbar from "./components/Navbar/Navbar.js";
import Introduction from "./components/Introduction/Introduction.js";
import ImageCollage from "./components/ImageCollage/ImageCollage.js";
import Skills from "./components/skills/skills.js";

import Projects from "./components/Projects/Projects.js"

import Work from "./components/Work/Work.js";

import Footer from "./components/Footer/Footer.js";

import './App.css';

function App() {

  const { theme, toggleTheme } = useTheme(); 

  return (

  <div className={`App ${theme}`}> {/* Use theme as a class */}
      <div className={`white_background ${theme === 'dark' ? 'dark_background' : ''}`}>
        <div className="App">
          <div class="white_background">

            <Navbar/>
            <button id="apperance" onClick={toggleTheme}>X</button>
            <Introduction/>
            <ImageCollage/>
            <Skills/>  
            {/* <Work/>
            <Projects/> */}
            <Footer/>

          </div>

          </div>   
    </div>
  </div>
  );
}

export default App;
