import React, { useEffect, useRef } from 'react';
import Typed from 'typed.js';

const typing = () => {
    const typed = useRef(null);
    const el = useRef(null);

    useEffect(() => {
      const options = {
        strings: [
          'First sentence.',
          'Second sentence.'
        ],
        typeSpeed: 50,
        backSpeed: 25,
        loop: true,
      };

      typed.current = new Typed(el.current, options);

      return () => {
        typed.current.destroy();
      };
    }, []);

    return <span ref={el} />;
  };

  export default typing;
