import React, { useEffect, useRef } from 'react';
import VanillaTilt from 'vanilla-tilt';

import './Projects.css';
import link from "../../assets/symbols/link.svg";
import autoscore from "../../assets/symbols/autoscore.png";
import memegame from "../../assets/symbols/memegame.jpg";
import portfolio from "../../assets/symbols/portfolio.jpg";

const Projects = () => {
  const projectRefs = useRef([]);

  useEffect(() => {
    projectRefs.current.forEach(ref => {
      VanillaTilt.init(ref, {
        max: 25,
        speed: 400,
        glare: true,
        "max-glare": 0.5,
      });
    });
  }, []);

  return (
    <section id='Body'>
      <div className="maxwidth-60 margin-title">
        <h1 className='title'>Projects</h1>
        <h1 className="paragraph">
          Here are a few personal projects.
        </h1>
      </div>

      <section className="Projects_Container">
        {[autoscore, memegame, portfolio].map((project, index) => (
          <div className="Project_items" key={index} ref={el => projectRefs.current[index] = el}>
            <h1 className="project_title">{project === autoscore ? 'AutoScore' : project === memegame ? 'Meme-Memory' : 'Portfolio Website'}</h1>
            <img className="project_image" src={project} alt="" />
            <p className="project_description">
              {project === autoscore ? 'This project aids teaching assistance by automating the process of grading assignments for a Java course, marking the code, and seamlessly submitting the grades to MyLearningSpace.' : project === memegame ? 'Meme-Memory is an innovative and fun memory game that combines the classic challenge of "Simon Says" with the vibrant culture of internet memes.' : 'Developed a portfolio web application tailored to showcase professional and creative work in a visually engaging and organized manner. Inspired by Tailwind UI Spotlight.'}
            </p>
            <div className="project_skills">
              {project === autoscore ? ['#JFrames', '#AbstractSyntaxTrees', '#JUnitTesting', '#Agile'].map(skill => <span key={skill}>{skill}</span>) : project === memegame ? ['#jQuery', '#HTML', '#CSS'].map(skill => <span key={skill}>{skill}</span>) : ['#React.js', '#Node.js', '#Firebase', '#InspiredByTailwindUI'].map(skill => <span key={skill}>{skill}</span>)}
            </div>
            <div className="buttons_container">
              <a className="github_link" href={project === autoscore ? 'https://github.com/kevinyejoonlee/AutoScore' : project === memegame ? 'https://github.com/kevinyejoonlee/Meme-Memory-Game' : 'https://github.com/kevinyejoonlee/to-do-list'}>
                <img className="linkImg" src={link} alt="" />
                <span>{project === autoscore ? 'github/autoscore' : project === memegame ? 'github/MemeGame' : 'github/kevinPortfolio'}</span>
              </a>
            </div>
          </div>
        ))}
      </section>
    </section>
  );
};

export default Projects;
