import React, { useEffect, useRef } from 'react';
import VanillaTilt from 'vanilla-tilt';

import './Projects.css';
import link from "../../assets/symbols/link.svg";
import autoscore from "../../assets/symbols/autoscore.png";
import memegame from "../../assets/symbols/memegame.jpg";
import portfolio from "../../assets/symbols/portfolio.jpg";

const Projects = () => {
  const tiltRef = useRef([]);

  useEffect(() => {
    tiltRef.current.forEach(tilt => {
      VanillaTilt.init(tilt, {
        max: 25,
        speed: 400,
        glare: true,
        "max-glare": 0.5,
      });
    });

    return () => {
      tiltRef.current.forEach(tilt => tilt.vanillaTilt.destroy());
    };
  }, []);

  // Function to add elements to the ref array
  const addToRef = el => {
    if (el && !tiltRef.current.includes(el)) {
      tiltRef.current.push(el);
    }
  };

  return (
    <section id='Body'>
      <div className="maxwidth-60 margin-title">
        <h1 className='title'>Projects</h1>
        <h1 className="paragraph">
          Here are a few personal projects.
        </h1>
      </div>

      <section className="Projects_Container">
        {/* Repeat this structure for each project item */}
        <div className="Project_items one" ref={addToRef}>
          {/* Project content */}
        </div>
        {/* Continue for other projects, make sure to add ref={addToRef} */}
      </section>
    </section>
  );
};

export default Projects;
