import React, { useState, useEffect } from 'react';
import './Navbar.css';
import { NavLink, Link } from 'react-router-dom'; // Import NavLink
import sun from "../../assets/symbols/sun.svg";
import moon from "../../assets/symbols/moon.svg";
import menuIcon from "../../assets/menu.svg"; // Add your menu icon asset path here

const Navbar = () => {

  const [theme, setTheme] = useState(localStorage.getItem('theme') || 'light');
  const [isMenuOpen, setIsMenuOpen] = useState(false); // New state for managing the menu toggle

  useEffect(() => {
    const icon = document.getElementById("toggle_icon");
    icon.src = theme === 'dark' ? moon : sun;
    document.body.classList.toggle("dark_theme", theme === 'dark');
    localStorage.setItem('theme', theme); 
  }, [theme]);

  const toggleTheme = () => {
    setTheme(prevTheme => {
      const newTheme = prevTheme === 'dark' ? 'light' : 'dark';
      localStorage.setItem('theme', newTheme); 
      return newTheme;
    });
  };

  const toggleMenu = () => { // New function to toggle the hamburger menu
    setIsMenuOpen(prevState => !prevState);
  };

  return (
    <nav className='Navbar'>
      <div className="menu-icon" onClick={toggleMenu}> {/* Hamburger menu icon */}
        <img src={menuIcon} alt="Menu" />
      </div>

      <div className={`desktopMenu ${isMenuOpen ? 'open' : ''}`}>
        <Link to="/" className="desktopMenuListItem Home" onClick={() => setIsMenuOpen(false)}>Home</Link>
        <NavLink to="/experience" className="desktopMenuListItem" activeClassName="activeLink" onClick={() => setIsMenuOpen(false)}>Experience</NavLink>
        <NavLink to="/projects" className="desktopMenuListItem" activeClassName="activeLink" onClick={() => setIsMenuOpen(false)}>Projects</NavLink>
        <NavLink to="/resume" className="desktopMenuListItem" activeClassName="activeLink" onClick={() => setIsMenuOpen(false)}>Resume</NavLink> {/* Updated to use a route instead of a direct link */}
      </div>

      <button onClick={toggleTheme} id="apperance_b">
        <img id="toggle_icon" className="apperance_icon" src={theme === 'light' ? sun : moon} alt="Toggle theme" />
      </button>
    </nav>
  );
};

export default Navbar;
